DROP VIEW [dbo].[v_tripdata_exttable];
GO;

CREATE VIEW [dbo].[v_tripdata_exttable]
AS
SELECT * FROM [dbo].[tripdata]
GO;

DROP VIEW [dbo].[v_tripdata_openrowset];
GO;

CREATE VIEW [dbo].[v_tripdata_openrowset]
AS
SELECT
    *
FROM
    OPENROWSET(
        BULK 'https://mpajn6axh2pc3wispoc.dfs.core.windows.net/public/trip-data.csv',
        FORMAT = 'CSV',
        PARSER_VERSION = '2.0',
        HEADER_ROW = TRUE
    ) AS [result]
GO;

DROP VIEW [dbo].[v_tripdata_extview];
GO;

CREATE VIEW [dbo].[v_tripdata_extview]
AS
SELECT * FROM [dbo].[v_tripdata_openrowset]
GO;
