content.query:
-- Check current user
SELECT CURRENT_USER; 
SELECT SUSER_NAME(), USER_NAME();  

-- Check owners
EXECUTE sp_tables [tripdata];
EXECUTE sp_tables [v_tripdata];

-- Create Login+User for test
CREATE LOGIN login1 WITH PASSWORD = 'J345#$)thb';  
CREATE USER user1 FOR LOGIN login1;

-- Grant user read rights on view but not on ext. table
GRANT SELECT ON [dbo].[v_tripdata] TO user1;

-- Check access to view for user 1
EXECUTE AS LOGIN = 'login1';
SELECT SUSER_NAME(), USER_NAME();
SELECT TOP 1 * FROM [dbo].[v_tripdata]; 
REVERT;

-- Check access to ext. table for user 1
EXECUTE AS LOGIN = 'login1';
SELECT SUSER_NAME(), USER_NAME();
SELECT TOP 1 * FROM [dbo].[tripdata]; 
REVERT;
