content.query:
-- Check current user
SELECT CURRENT_USER; 
SELECT SUSER_NAME(), USER_NAME();  

-- Check owners
EXECUTE sp_tables [tripdata];
EXECUTE sp_tables [v_tripdata];

-- Create Login+User for test
CREATE LOGIN login1 WITH PASSWORD = 'J345#$)thb';  
CREATE USER user1 FOR LOGIN login1;

-- Grant user read rights on view but not on ext. table
GRANT SELECT ON [dbo].[v_tripdata_exttable] TO user1;

-- Check access to view for user 1
EXECUTE AS LOGIN = 'login1';
--SELECT SUSER_NAME(), USER_NAME();
SELECT TOP 10 SUSER_NAME(), USER_NAME(), * FROM [dbo].[v_tripdata_exttable]; 
REVERT;

-- Check access to ext. table for user 1
EXECUTE AS LOGIN = 'login1';
--SELECT SUSER_NAME(), USER_NAME();
SELECT TOP 10 SUSER_NAME(), USER_NAME(), * FROM [dbo].[tripdata]; 
REVERT;

-- Grant user read rights on view but not on underlying openrowset view
GRANT SELECT ON [dbo].[v_tripdata_extview] TO user1;

-- Check access to view for user 1
EXECUTE AS LOGIN = 'login1';
--SELECT SUSER_NAME(), USER_NAME();
SELECT TOP 10 SUSER_NAME(), USER_NAME(), * FROM [dbo].[v_tripdata_extview]; 
REVERT;

-- Check access to ext. view for user 1
EXECUTE AS LOGIN = 'login1';
--SELECT SUSER_NAME(), USER_NAME();
SELECT TOP 10 SUSER_NAME(), USER_NAME(), * FROM [dbo].[v_tripdata_openrowset]; 
REVERT;

-- Check if user can hack openrowset
EXECUTE AS LOGIN = 'login1';
--SELECT SUSER_NAME(), USER_NAME();
SELECT TOP 10 SUSER_NAME(), USER_NAME(), *
FROM
    OPENROWSET(
        BULK 'https://mpajn6axh2pc3wispoc.dfs.core.windows.net/public/trip-data.csv',
        FORMAT = 'CSV',
        PARSER_VERSION = '2.0',
        HEADER_ROW = TRUE
    ) AS [result];
REVERT;

DROP USER user1;
DROP LOGIN login1;
